package java01;


public class jv02_10_산술연산 {

    
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        
        int x = '4';
        int y = '5';
        int result;
        
        result = x + y;
        System.out.println(result);
              
        result = x - y;
        System.out.println(result);
        
        result = x * y;
        System.out.println(result);

        result = x / y;
        System.out.println(result);

        result = x % y;
        System.out.println(result);
        
    }
}
